import { useSelector } from "react-redux";
import Card from '../card/Card';

const  Favorites =()=>{

  const onClose = (id) => {
    let deleted = characters.filter((elem) => elem.id !== Number(id))
    setCharacters(deleted);
 }
 
  const favorit = useSelector((state)=> state.myFavorites);
  return (
    <>
    {
      favorit.map(({id, name, status,species, gender, origin, image})=>{
        return (
          <Card 
                   key={id}
                   id={id}
                   name={name}
                   status={status}
                   species={species}
                   gender={gender}
                   origin={origin.name}
                   image={image}
                   
                   />
        )
      })
    }
    </>
  )
}

export default Favorites;
