import { ADD_FAVORITE, REMOVE_FAVORITE, FILTER, ORDER } from './actions.js';


const initialState = {
  myFavorites:[],
  allCharacters: [],
};

const rootReducer = (state = initialState, action)=> {

    switch (action.type) {
      case ADD_FAVORITE:
        return {...state, 
          myFavorites:[...state.allCharacters, action.payload],
          allCharacters:[...state.allCharacters, action.payload],
              };
        
        case REMOVE_FAVORITE:
          return {...state, 
            myFavorites: state.myFavorites.filter((char)=>char.id !== action.payload) }

        case FILTER:
          const filterFavor = state.allCharacters.filter((char)=>
          char.gender === action.payload);

           return {...state, 
            myFavorites: action.payload === action.allCharacters
             ? [...state.allCharacters] : filterFavor
          };

          case ORDER:
            const orderFavor = [...state.allCharacters];
            return {...state,
              myFavorites: action.payload === 'A' 
              ? orderFavor.sort((a,b)=> a.id - b.id)
              : orderFavor.sort((a,b)=> b.id - a.id)
            };
            
      default:
        return {...state};
    }
};

export default rootReducer;
